import React, { useState } from 'react';
import styled from 'styled-components';

// Styled Components for styling
const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  padding: 20px;
  background-color: #f0f8ff;
`;

const Content = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  max-width: 1200px;
  height: 100%;
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const ImageContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const StyledImage = styled.img`
  width: 100%;
  max-width: 500px;
  height: auto;
  border-radius: 10px;
`;

const StyledButton = styled.button`
  margin-top: 20px;
  padding: 10px 20px;
  font-size: 18px;
  background-color: #ff5722;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  &:hover {
    background-color: #e64a19;
  }
`;

const IframeContainer = styled.div`
  flex: 1;
  height: 500px;
  display: ${({ show }) => (show ? 'block' : 'none')};
  width: 100%;
  max-width: 600px;
`;

const AIFrame = styled.iframe`
  width: 100%;
  height: 100%;
  border: 2px solid #333;
  border-radius: 10px;
`;

import image from "./assets/codingal_image-1.jpg"

function App() {
  const [showIframe, setShowIframe] = useState(false);

  return (
    <Container>
      <Content>
        <ImageContainer>
          <StyledImage
            src={image} // Replace with your image URL
            alt="Kids Coding"
          />
          <StyledButton onClick={() => setShowIframe(true)}>Talk to AI Assistant</StyledButton>
        </ImageContainer>

        <IframeContainer show={showIframe}>
          {showIframe && (
            <AIFrame
              src="https://your-ai-assistant-url.com" // Replace with your AI iframe URL
              title="AI Assistant"
            />
          )}
        </IframeContainer>
      </Content>
    </Container>
  );
}

export default App;
