import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import image from "./assets/students_learning.jpg";

// Main container for styling
const Container = styled.div`
  padding: 0;
  margin: 0;
  font-family: 'Arial', sans-serif;
  background: #fff; /* White background */
  color: #000; /* Black text color */
  min-height: 100vh;
`;

const Header = styled.header`
  background-color: #ff5722; /* Orange header */
  padding: 20px;
  color: white; /* White text */
  text-align: center;
  font-size: 40px;
  font-weight: bold;
`;

const Section = styled.div`
  padding: 40px;
  text-align: center;
`;

const Title = styled.h2`
  font-size: 36px;
  margin-bottom: 20px;
  color: #000; /* Black title */
`;

const Description = styled.p`
  font-size: 20px;
  margin-bottom: 40px;
  color: #000; /* Black text */
`;

const Button = styled.button`
  padding: 15px 30px;
  background-color: #ff5722; /* Orange button */
  color: white; /* White text */
  border: none;
  border-radius: 30px;
  cursor: pointer;
  font-size: 20px;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.3);
  }
`;

const CourseList = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* 3 cards per row */
  gap: 30px;
  margin-top: 40px;

  @media (max-width: 768px) {
    grid-template-columns: repeat(2, 1fr); /* 2 cards per row on tablets */
  }

  @media (max-width: 480px) {
    grid-template-columns: 1fr; /* 1 card per row on mobile */
  }
`;

const CourseItem = styled.div`
  background-color: #fff;
  border: 2px solid #ff5722; /* Orange border */
  border-radius: 15px;
  padding: 20px;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0px 10px 25px rgba(0, 0, 0, 0.2);
  }
`;

const CourseTitle = styled.h3`
  font-size: 24px;
  margin-bottom: 10px;
  color: #000; /* Black title */
`;

const CourseDescription = styled.p`
  font-size: 18px;
  color: #000; /* Black text */
`;

const ImageContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledImage = styled.img`
  width: 60%;  /* Default size for larger screens */
  border-radius: 20px;
  margin: 20px 0;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.2);
  }

  @media (max-width: 768px) {
    width: 80%;  /* Slightly smaller size for tablets */
  }

  @media (max-width: 480px) {
    width: 90%;  /* Best size for mobile devices */
  }
`;

const Popup = styled.div`
  display: ${({ show }) => (show ? 'block' : 'none')};
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: white;
  padding: 40px;
  border-radius: 10px;
  box-shadow: 0px 10px 40px rgba(0, 0, 0, 0.3);
  z-index: 10;
  width: 80%;
  max-width: 800px;
`;

const IframeContainer = styled.div`
  width: 100%;
  height: 400px;
  border-radius: 10px;
  border: none;
  position: relative;
  cursor: pointer;
`;

const Iframe = styled.iframe`
  width: 100%;
  height: 100%;
  border-radius: 10px;
  border: none;
`;

const CountdownText = styled.p`
  font-size: 24px;
  color: #000;
  text-align: center;
`;

function App() {
  const [showPopup, setShowPopup] = useState(false);
  const [countdown, setCountdown] = useState(null); // Countdown starts as null
  const [isCountdownActive, setIsCountdownActive] = useState(false); // Control the countdown visibility
  const iframeRef = useRef(null);
  const popupRef = useRef(null); // Ref to track the popup for outside click detection

  const requestMicrophoneAccess = async () => {
    try {
      await navigator.mediaDevices.getUserMedia({ audio: true });
      console.log('Microphone access granted');
      // Send a message to iframe to start voice recognition
      if (iframeRef.current) {
        const iframeWindow = iframeRef.current.contentWindow;
        iframeWindow.postMessage("Start voice recognition", '*');
      }
    } catch (error) {
      console.error('Error accessing microphone:', error);
    }
  };

  const handleOpenPopup = () => {
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  // Detect clicks outside the popup and close it
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (popupRef.current && !popupRef.current.contains(event.target)) {
        handleClosePopup();
      }
    };

    if (showPopup) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showPopup]);

  // Countdown logic after the iframe is clicked
  const handleIframeClick = () => {
    if (!isCountdownActive) {
      setIsCountdownActive(true);
      setCountdown(5); // Start countdown from 5 when clicked for the first time
    }
  };

  // Decrease countdown each second
  useEffect(() => {
    if (countdown > 0 && isCountdownActive) {
      const timer = setTimeout(() => {
        setCountdown((prev) => prev - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (countdown === 0) {
      requestMicrophoneAccess(); // Trigger microphone access once countdown is finished
      setIsCountdownActive(false);
    }
  }, [countdown, isCountdownActive]);

  return (
    <Container>
      <Header>Codingal</Header>

      {/* Section 1 - Introduction */}
      <Section>
        <Title>Online Coding & AI Classes for Kids and Teens</Title>
        <Description>
          Become the innovators of tomorrow with engaging live video lessons taught by world-class instructors.
        </Description>
        <Button onClick={handleOpenPopup}>Talk to AI Assistant</Button>
      </Section>

      {/* Section 2 - Courses */}
      <Section>
        <Title>Our Bestselling Courses</Title>
        <CourseList>
          <CourseItem>
            <CourseTitle>Coding Grandmaster</CourseTitle>
            <CourseDescription>
              144 Lessons to master coding, build apps, games, and websites. Accredited by STEM.org
            </CourseDescription>
          </CourseItem>
          <CourseItem>
            <CourseTitle>Coding Prodigy</CourseTitle>
            <CourseDescription>
              96 Lessons designed for mastering coding skills, accredited by STEM.org
            </CourseDescription>
          </CourseItem>
          <CourseItem>
            <CourseTitle>Python Champion</CourseTitle>
            <CourseDescription>
              44 Lessons to master Python through interactive games, GUIs, and more.
            </CourseDescription>
          </CourseItem>
          <CourseItem>
            <CourseTitle>Game Development</CourseTitle>
            <CourseDescription>
              Learn to code games using Scratch and JavaScript in 36 lessons!
            </CourseDescription>
          </CourseItem>
          <CourseItem>
            <CourseTitle>Data Science for Kids</CourseTitle>
            <CourseDescription>
              Learn data science concepts and hands-on projects with Python and analytics skills.
            </CourseDescription>
          </CourseItem>
          <CourseItem>
            <CourseTitle>App Development</CourseTitle>
            <CourseDescription>
              50+ Activities to create AI-powered apps. Learn to build and share amazing apps.
            </CourseDescription>
          </CourseItem>
        </CourseList>
      </Section>

      {/* Image Section */}
      <Section>
        <Title>Children Learning to Code</Title>
        <ImageContainer>
          <StyledImage src={image} alt="Children learning to code" />
        </ImageContainer>
      </Section>

      {/* AI Assistant Popup */}
      <Popup show={showPopup} ref={popupRef}>
        <IframeContainer onClick={handleIframeClick}>
          <Iframe
            src="https://vapi.ai?demo=true&shareKey=fe502c7f-d3a8-46bf-a2d8-aea6eb0d1ea0&assistantId=b5b17e14-1123-478f-a6e1-a1e7c343dcd5"
            title="AI Assistant"
            allow="microphone"
            ref={iframeRef}
          />
        </IframeContainer>
        {isCountdownActive && countdown !== null ? (
          <CountdownText>Connecting to AI Assistant in {countdown}...</CountdownText>
        ) : null}
        <Button onClick={handleClosePopup}>Close</Button>
      </Popup>
    </Container>
  );
}

export default App;
